package Javalevel1;

public class Whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n=1; 
		while (n <= 100) 
                      if (n % 11 == 0) {
                     System.out.println(n);
                   }
                    n = n + 1;
	}

	}


